
public class DataTypesTest
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

	int rollno=256;
	System.out.println("rollno is:"+rollno);
	System.out.println("integer min value is: "+Integer.MIN_VALUE);
	System.out.println("integer max value is: "+Integer.MAX_VALUE);
	System.out.println("integer size value is: "+Integer.SIZE);
	System.out.println("-------------------------------------------");
	
	long rollNumber=256;
	System.out.println("rollNumber is:"+rollNumber);
	System.out.println("long min value is: "+Long.MIN_VALUE);
	System.out.println("long max value is: "+Long.MAX_VALUE);
	System.out.println("long size value is: "+Long.SIZE);
	System.out.println("-------------------------------------------");

	float salary=25000.25f;
	System.out.println("salary is: "+salary);
	System.out.println("float min value is: "+Float.MIN_VALUE);
	System.out.println("float max value is: "+Float.MAX_VALUE);
	System.out.println("float SIZE value is: "+Float.SIZE);
	System.out.println("-------------------------------------------");

	boolean completedAssignment=true;
	if (completedAssignment)
	System.out.println("yes! completed assignment");
	else
	System.out.println("no, not completted assignment");
	System.out.println("-------------------------------------------");

	
	char ch='Y';
	System.out.println("ch is  :" +ch);
	System.out.println("char min value is: "+Character.MIN_VALUE);
	System.out.println("char max value is: "+Character.MAX_VALUE);
	System.out.println("char size value is: "+Character.SIZE);
	System.out.println("-------------------------------------------");

	double molecularDistance=0.251025f;
	System.out.println("molecularDistance is  :" +molecularDistance);
	System.out.println("double min value is: "+Double.MIN_VALUE);
	System.out.println("double min value is: "+Double.MAX_VALUE);
	System.out.println("double size value is: "+Double.SIZE);
	System.out.println("-------------------------------------------");

	byte b=13;
	System.out.println("byte is:"+b);
	System.out.println("byte min value is: "+Byte.MIN_VALUE);
	System.out.println("byte max value is: "+Byte.MAX_VALUE);
	System.out.println("byte size value is: "+Byte.SIZE);
}



	
	
	

	






	
	
	

}
